const template=document.createElement("template");template.innerHTML="\n<style>\n    :host{\n        display block;\n        font-family: sans-serif;\n        text-align: center\n    }\n    \n    button {\n        border: 2px solid;\n        cursor: pointer;\n    }\n</style>\n<button> Im a Button </button>\n";class BeerButton extends HTMLElement{constructor(){super(),this._shadowRoot=this.attachShadow({mode:"open"}),this._shadowRoot.appendChild(template.content.cloneNode(!0)),this.$beerButton=this._shadowRoot.querySelector("button"),this.hasAttribute("disable")||(this.hasAttribute("link")?this.$beerButton.addEventListener("click",this.linkFunction.bind(this)):this.hasAttribute("increment")?(this.inc=!0,this.$beerButton.addEventListener("click",this.incrementDecrementFunction.bind(this))):this.hasAttribute("decrement")&&(this.inc=!1,this.$beerButton.addEventListener("click",this.incrementDecrementFunction.bind(this))))}linkFunction(){var t=this.getAttribute("link");t.length<=0?console.log("Invalid Link"):window.location.href=t}incrementDecrementFunction(){var t;if(this.inc){var e=this.getAttribute("increment");e<=0?console.log("Invalid input"):(t=parseInt(document.getElementById(e).value,10),t++,document.getElementById(e).value=t)}else{var n=this.getAttribute("decrement");n<=0&&console.log("Invalid input"),t=parseInt(document.getElementById(n).value,10),t--,document.getElementById(n).value=t}}}window.customElements.define("beer-button",BeerButton);